import React from 'react';

import whatsappIcon from  '../../assets/images/icons/whatsapp.svg'; 

import "./styles.css";

interface TeacherItemProps {
    teacher: {
        id: number;
        avatars: string;
        bio: string;
        cost: number;
        name: string;
        subject: string;
        whatsapp: string;
    };
}

const TeacherItem: React.FC<TeacherItemProps> = ({ teacher }) => {
    return (
        <article className="teacher-item">
            <header>
                <img src="https://avatars2.githubusercontent.com/u/2254731?s=460&u=0ba16a79456c2f250e7579cb388fa18c5c2d7d65&v=4" alt="Thiago Machado" />
                    <div>
                        <strong>{teacher.name}</strong>
                        <span>`{teacher.subject}</span>
                    </div>
            </header>

            <p>
                Entusiasta das melhores tecnologias de química avançada.
                <br /><br />
                Apaixonado por explodir coisas em laboratório e por mudar a vida das pessoas através das experiências.
            </p>    

            <footer>
                <p>
                    Preço/Hora
                    <strong>R$ 20</strong>
                </p>
                <button type="button">
                    <img src={whatsappIcon} alt="Whatsapp"/>
                    Entrar em contato
                </button>
            </footer>    
        </article>
    );
}

export default TeacherItem;
